function UserProfile(props) {

  let user = useSelector((state) => state.user);
  const { id, email, first_name, last_name, avatar } = user;
  const dispatch = useDispatch();
  const fetchProductDetail = async () => {
    const response = await axios
      .get(`https://reqres.in/api/users/${props.userID}`)
      .catch((err) => {
        console.log("Err: ", err);
      });
    dispatch(selectedProduct(response.data.data));
  };

  useEffect(() => {
    if(props.userID!=0){
       fetchProductDetail(props.userID);
    }

      }, [props.userID]);


  return (
    <div className="userCard">
    {props.userID==0 &&<div className="content">
                <div className="userName">Click on a user!</div>
                <div className="userEmail">to get information</div>

             </div>}

        <div className="content">
                <div className="userName">{user.first_name} {user.last_name}</div>
                <div className="userEmail">@ {user.email}</div>

             </div>
             <div className="image">
                <img src={user.avatar} alt={id} />
              </div>
             </div>
  )
}

export default UserProfile
